
public class Casting {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a, b;
		double resultado;
		a = 5;
		b = 2;
		resultado = (double) a / b; // realizado o casting, que é a conversão das variaveis na espressão
		System.out.println(resultado); // Divisão de inteiros não divide numeros impares

	}

}
